import Layout from '../Layout/Layout'
import React, {useEffect, useState} from 'react'
import axios from 'axios'
import './Nature-Retreat.css'
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const NatureRetreat = () => {

    // State to store the fetched data
    const [listingData, setListingData] = useState([]);

    const [date, setDate] = useState(new Date());

    // const [listingData, setListingData] = useState([]);

    // Function to fetch data from the API
    const fetchData = () => {
        axios.get('http://localhost:8080/rooms/nature-retreat')
            .then(response => {
                if (response.status !== 200) {
                    throw new Error('Network response was not ok: ' + response.data);
                }
                return response.data;
            })
            .then(data => {
                setListingData(data);
            })
            .catch(error => {
                console.error('There was a problem with the fetch operation:', error);
            });
    };

    // Fetch data when the component mounts
    useEffect(() => {
        fetchData();
    }, []); // Empty dependency array ensures useEffect runs only once on mount

    const [selectedRoom, setSelectedRoom] = useState('single');

    const handleRoomChange = (e) => {
        setSelectedRoom(e.target.value);
    };


    return (
        <div>
            <Layout/>


            <div className='listing-name'>
                {listingData.name}
            </div>

            <div className='listing-description'>
                {listingData.description}
            </div>

            <div>
                <img src={listingData.imageURL} className='listing-img'/>
            </div>

            <p className='check-in-label'>Check-in Date:</p>
            <div className='check-in-date'>
                <DatePicker wrapperClassName='date-test1' selected={date} onChange={date => setDate(date)}/>
            </div>

            <p className='check-out-label'>Check-out Date:</p>
            <div className='check-out-date'>
                <DatePicker selected={date} onChange={date => setDate(date)}/>
            </div>

            <div>
                <label htmlFor="room-select">Select Room Type:</label>
                <select id="room-select" value={selectedRoom} onChange={handleRoomChange}>
                    <option value="single">Single Room</option>
                    <option value="double">Double Room</option>
                    <option value="suite">Suite</option>
                    {/* Add more options for other room types */}
                </select>
                <p>Selected Room Type: {selectedRoom}</p>
            </div>


        </div>
    );

}

export default NatureRetreat;
